import React, { useState, useEffect } from 'react';

const PurchaseManagement = () => {
  const [purchases, setPurchases] = useState(() => {
    const saved = localStorage.getItem('purchases');
    return saved ? JSON.parse(saved) : [];
  });

  const [purchaseForm, setPurchaseForm] = useState({
    date: '',
    productCode: '',
    productName: '',
    spec: '',
    unit: '',
    price: '',
    supplier: '',
    note: ''
  });

  const [editIndex, setEditIndex] = useState(null);

  const fieldLabels = {
    date: '날짜',
    productCode: '제품코드',
    productName: '제품명',
    spec: '규격',
    unit: '단위',
    price: '단가',
    supplier: '매입처',
    note: '비고'
  };

  useEffect(() => {
    localStorage.setItem('purchases', JSON.stringify(purchases));
  }, [purchases]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setPurchaseForm((prev) => ({ ...prev, [name]: value }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (editIndex !== null) {
      const updated = [...purchases];
      updated[editIndex] = purchaseForm;
      setPurchases(updated);
      setEditIndex(null);
    } else {
      setPurchases([...purchases, purchaseForm]);
    }
    setPurchaseForm({
      date: '',
      productCode: '',
      productName: '',
      spec: '',
      unit: '',
      price: '',
      supplier: '',
      note: ''
    });
  };

  const handleEdit = (index) => {
    setPurchaseForm(purchases[index]);
    setEditIndex(index);
  };

  const handleDelete = () => {
    if (editIndex !== null) {
      const updated = purchases.filter((_, i) => i !== editIndex);
      setPurchases(updated);
      setEditIndex(null);
      setPurchaseForm({
        date: '',
        productCode: '',
        productName: '',
        spec: '',
        unit: '',
        price: '',
        supplier: '',
        note: ''
      });
    }
  };

  const handleSaveToFile = () => {
    const blob = new Blob([JSON.stringify(purchases, null, 2)], { type: 'application/json' });
    const a = document.createElement('a');
    a.href = URL.createObjectURL(blob);
    a.download = 'purchases.json';
    a.click();
  };

  const handleLoadFromFile = (e) => {
    const fileReader = new FileReader();
    fileReader.onload = (event) => {
      const data = JSON.parse(event.target.result);
      setPurchases(data);
    };
    fileReader.readAsText(e.target.files[0]);
  };

  return (
    <div style={{ maxWidth: '1000px', margin: 'auto', padding: '20px' }}>
      <h1>매입 자료 관리</h1>

      <form onSubmit={handleSubmit} style={{ display: 'grid', gridTemplateColumns: '150px 1fr', gap: '8px 16px', alignItems: 'center' }}>
        {Object.entries(purchaseForm).map(([key, value]) => (
          <React.Fragment key={key}>
            <label htmlFor={key} style={{ textAlign: 'right' }}>{fieldLabels[key]}</label>
            <input
              id={key}
              name={key}
              value={value}
              onChange={handleChange}
              type={key === 'date' ? 'date' : 'text'}
              required={['date', 'productCode', 'productName', 'price'].includes(key)}
              style={{ padding: '6px', width: '250px' }}
            />
          </React.Fragment>
        ))}
        <div style={{ gridColumn: '2', textAlign: 'right' }}>
          <button type="submit" style={{ marginRight: '8px' }}>{editIndex !== null ? '수정' : '등록'}</button>
          {editIndex !== null && <button type="button" onClick={handleDelete}>삭제</button>}
        </div>
      </form>

      <div style={{ marginTop: '20px' }}>
        <input type="file" accept=".json" onChange={handleLoadFromFile} />
        <button onClick={handleSaveToFile}>저장</button>
      </div>

      <table border="1" cellPadding="5" style={{ width: '100%', marginTop: '20px', fontSize: '14px' }}>
        <thead>
          <tr>
            {Object.keys(fieldLabels).map((key) => (
              <th key={key}>{fieldLabels[key]}</th>
            ))}
            <th>수정</th>
          </tr>
        </thead>
        <tbody>
          {purchases.map((item, index) => (
            <tr key={index}>
              {Object.keys(fieldLabels).map((key) => (
                <td key={key}>{item[key]}</td>
              ))}
              <td><button onClick={() => handleEdit(index)}>수정</button></td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default PurchaseManagement;
