import React, { useState, useEffect } from 'react';

const emptyForm = {
  date: '',
  productCode: '',
  productName: '',
  spec: '',
  unit: '',
  quantity: '',
  price: '',
  supplier: '',
  note: ''
};

const fieldLabels = {
  date: '날짜',
  productCode: '제품코드',
  productName: '제품명',
  spec: '규격',
  unit: '단위',
  quantity: '수량',
  price: '단가',
  supplier: '매입처',
  note: '창고명'
};

const PurchaseManagement = () => {
  const [purchases, setPurchases] = useState(() => {
    const saved = localStorage.getItem('purchases');
    return saved ? JSON.parse(saved) : [];
  });

  const [purchaseForm, setPurchaseForm] = useState(emptyForm);
  const [editIndex, setEditIndex] = useState(null);
  const [searchTerm, setSearchTerm] = useState('');

  useEffect(() => {
    localStorage.setItem('purchases', JSON.stringify(purchases));
  }, [purchases]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setPurchaseForm((prev) => ({ ...prev, [name]: value }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    const quantity = parseFloat(purchaseForm.quantity);
    const price = parseFloat(purchaseForm.price);

    if (isNaN(quantity) || isNaN(price)) {
      alert('수량과 단가는 숫자여야 합니다.');
      return;
    }

    const newEntry = {
      ...purchaseForm,
      quantity,
      price
    };

    if (editIndex !== null) {
      const updated = [...purchases];
      updated[editIndex] = newEntry;
      setPurchases(updated);
      setEditIndex(null);
    } else {
      setPurchases([...purchases, newEntry]);
    }

    setPurchaseForm(emptyForm);
  };

  const handleEdit = (index) => {
    const item = purchases[index];
    setPurchaseForm({
      ...item,
      quantity: item.quantity.toString(),
      price: item.price.toString()
    });
    setEditIndex(index);
  };

  const handleDelete = () => {
    if (editIndex !== null) {
      const updated = purchases.filter((_, i) => i !== editIndex);
      setPurchases(updated);
      setEditIndex(null);
      setPurchaseForm(emptyForm);
    }
  };

  const handleSaveToFile = () => {
    const blob = new Blob([JSON.stringify(purchases, null, 2)], { type: 'application/json' });
    const a = document.createElement('a');
    a.href = URL.createObjectURL(blob);
    a.download = 'purchases.json';
    a.click();
  };

  const handleLoadFromFile = (e) => {
    const file = e.target.files[0];
    if (!file) return;

    const reader = new FileReader();
    reader.onload = (event) => {
      try {
        let data = JSON.parse(event.target.result);
        data = data.map(item => ({
          date: item.date || '',
          productCode: item.productCode || '',
          productName: item.productName || '',
          spec: item.spec || '',
          unit: item.unit || '',
          quantity: parseFloat(item.quantity) || 0,
          price: parseFloat(item.price) || 0,
          supplier: item.supplier || '',
          note: item.note || ''
        }));
        setPurchases(data);
        setSearchTerm('');
      } catch (err) {
        alert('올바르지 않은 JSON 파일입니다.');
      }
    };
    reader.readAsText(file);
  };

  const filteredPurchases = purchases.filter((item) => {
    const term = searchTerm.toLowerCase();
    return (
      item.productCode.toLowerCase().includes(term) ||
      item.productName.toLowerCase().includes(term)
    );
  });

  const formatNumber = (num) =>
    num.toLocaleString(undefined, {
      minimumFractionDigits: 2,
      maximumFractionDigits: 2
    });

  const totalQuantity = filteredPurchases.reduce((sum, item) => sum + item.quantity, 0);
  const totalAmount = filteredPurchases.reduce((sum, item) => sum + item.quantity * item.price, 0);

  return (
    <div style={{ maxWidth: '1000px', margin: 'auto', padding: '20px' }}>
      <h1>매입 자료 관리</h1>

      <form
        onSubmit={handleSubmit}
        style={{
          display: 'grid',
          gridTemplateColumns: '150px 1fr',
          gap: '8px 16px',
          alignItems: 'center'
        }}
      >
        {Object.entries(purchaseForm).map(([key, value]) => (
          <React.Fragment key={key}>
            <label htmlFor={key} style={{ textAlign: 'right' }}>{fieldLabels[key]}</label>
            <input
              id={key}
              name={key}
              value={value}
              onChange={handleChange}
              type={
                key === 'date'
                  ? 'date'
                  : ['quantity', 'price'].includes(key)
                  ? 'number'
                  : 'text'
              }
              required={['date', 'productCode', 'productName', 'quantity', 'price'].includes(key)}
              step={['quantity', 'price'].includes(key) ? '0.01' : undefined}
              style={{ padding: '6px', width: '250px' }}
            />
          </React.Fragment>
        ))}
        <div style={{ gridColumn: '2', textAlign: 'left' }}>
          {editIndex !== null && (
            <button type="button" onClick={handleDelete} style={{ marginRight: '8px' }}>
              삭제
            </button>
          )}
          <button type="submit">{editIndex !== null ? '수정' : '등록'}</button>
        </div>
      </form>

      <div style={{ marginTop: '20px' }}>
        <input type="file" accept=".json" onChange={handleLoadFromFile} />
        <button onClick={handleSaveToFile}>저장</button>
      </div>

      <div style={{ marginTop: '20px' }}>
        <input
          type="text"
          placeholder="제품명 또는 코드로 검색"
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
          style={{ padding: '6px', width: '300px' }}
        />
      </div>

      <table border="1" cellPadding="5" style={{ width: '100%', marginTop: '20px', fontSize: '14px' }}>
        <thead>
          <tr>
            {Object.keys(fieldLabels).map((key) => (
              <th key={key}>{fieldLabels[key]}</th>
            ))}
            <th>수정</th>
          </tr>
        </thead>
        <tbody>
          {filteredPurchases.map((item, index) => (
            <tr key={index}>
              {Object.keys(fieldLabels).map((key) => (
                <td key={key}>
                  {['quantity', 'price'].includes(key)
                    ? formatNumber(item[key])
                    : item[key]}
                </td>
              ))}
              <td>
                <button onClick={() => handleEdit(index)}>수정</button>
              </td>
            </tr>
          ))}
        </tbody>
        
        <tfoot>
  <tr>
    <td colSpan={Object.keys(fieldLabels).length + 1} style={{ textAlign: 'right', fontWeight: 'bold' }}>
      총 {filteredPurchases.length}건 / 수량 {formatNumber(totalQuantity)} / 금액 {formatNumber(totalAmount)} 원
    </td>
  </tr>
</tfoot>

      </table>
    </div>
  );
};

export default PurchaseManagement;
